<?php

// phpcs:disable PSR1.Files.SideEffects

namespace Tymy\Module\Autotest\User;

use Tymy\Bootstrap;
use Tymy\Module\User\Model\User;
use Tymy\Module\Autotest\Entity\Assert;
use Tymy\Module\Autotest\RequestCase;
use Tymy\Module\Autotest\SimpleResponse;

require getenv("ROOT_DIR") . '/app/Bootstrap.php';
$container = Bootstrap::boot();

/**
 * Description of UserTest
 *
 * @author kminekmatej, 24.10.2020 14:00:05
 *
 */
class UserTest extends RequestCase
{
    private $inited = false;

    private function init()
    {
        if (!$this->inited) {
            $this->database->table(User::TABLE)->where("id > ?", 6)->where("status", "INIT")->delete();
            $this->inited = true;
        }
    }

    public function getModule(): string
    {
        return User::MODULE;
    }

    /**
     *
      @RequestMapping(value = "/users/{id}", method = RequestMethod.PUT)
      @RequestMapping(value = "/users/{id}/avatar", method = RequestMethod.POST)
      @RequestMapping(value = "/users", method = RequestMethod.POST)
      @RequestMapping(value = "/users/status/{status}", method = RequestMethod.GET)
      @RequestMapping(value = "/users/register", method = RequestMethod.POST)
      @RequestMapping(value = "/login/{userName:.+}/{password:.+}", method = RequestMethod.GET)
      @RequestMapping(value = "/loginTk", method = RequestMethod.GET)
      @RequestMapping(value = "/loginPhp", method = RequestMethod.GET)
      @RequestMapping(value = "/login", method = RequestMethod.POST)
      @RequestMapping(value = "/logout")
     *
     */
    public function testGetSingular()
    {
        $listResponse = $this->getList();
        if (count($listResponse->getData()) == 0) {
            return;
        }
        $data = $listResponse->getData();
        shuffle($data);
        $iterations = min(5, count($data));

        for ($index = 0; $index < $iterations; $index++) {
            $d = array_shift($data);
            $idRecord = $d["id"];
            $responseObj = $this->request($this->getBasePath() . "/$idRecord")->expect(200, "array");
        }
    }

    public function testGetPlural()
    {
        $listResponse = $this->request($this->getBasePath() . "s")->expect(200, "array");
        if (count($listResponse->getData()) == 0) {
            return;
        }
        $data = $listResponse->getData();
        shuffle($data);
        $iterations = min(5, count($data));

        for ($index = 0; $index < $iterations; $index++) {
            $d = array_shift($data);
            $idRecord = $d["id"];
            $responseObj = $this->request($this->getBasePath() . "s/$idRecord")->expect(200, "array");
        }
    }

    public function testLogin()
    {
        $this->user->logout(); //make sure user is logged out

        $username = $this->config["user_test_login"];
        $pwdHash = $this->config["user_test_pwd"];

        $this->request("login/$username/$pwdHash")->expect(200, "array");

        //test unknown username
        $this->request("login/skdfjbsdsfk/$pwdHash")->expect(401);

        for ($index = 1; $index <= 20; $index++) {//20 hashes shall success
            $pwdHash = md5($pwdHash);
            $this->request("login/$username/$pwdHash")->expect(200, "array");
        }

        //21st hash shall fail
        $pwdHash = md5($pwdHash);
        $this->request("login/$username/$pwdHash")->expect(401);
    }

    public function testStatus()
    {
        $this->authorizeAdmin();

        $players = $this->request($this->getBasePath() . "/status/PLAYER")->expect(200, "array")->getData();
        foreach ($players as $player) {
            Assert::equal("PLAYER", $player["status"]);
        }
        $members = $this->request($this->getBasePath() . "/status/MEMBER")->expect(200, "array")->getData();
        foreach ($members as $member) {
            Assert::equal("MEMBER", $member["status"]);
        }
        $inits = $this->request($this->getBasePath() . "/status/INIT")->expect(200, "array")->getData();
        foreach ($inits as $init) {
            Assert::equal("INIT", $init["status"]);
        }
        $sicks = $this->request($this->getBasePath() . "/status/SICK")->expect(200, "array")->getData();
        foreach ($sicks as $sick) {
            Assert::equal("SICK", $sick["status"]);
        }
        $this->request($this->getBasePath() . "/status")->expect(404);
        $this->request($this->getBasePath() . "/status/XSFD")->expect(404);
    }

    public function testCRUD()
    {
        $recordId = $this->createRecord();

        $readResponse = $this->request($this->getBasePath() . "/" . $recordId)->expect(200, "array");

        $this->change($recordId);

        $this->request($this->getBasePath() . "/$recordId", "DELETE")->expect(405);

        $this->deleteRecord($recordId);
    }

    public function testCreateFailures()
    {
        $this->authorizeUser();

        $data = $this->mockRecord();
        $this->request(User::MODULE, "POST", $data)->expect(403);

        $this->authorizeAdmin();
        $data["login"] = $this->config["user_test_login"];
        $this->request(User::MODULE, "POST", $data)->expect(400);

        $data = $this->mockRecord();
        $data["email"] = $this->config["user_test_mail"];
        $this->request(User::MODULE, "POST", $data)->expect(400);

        unset($data["password"]);
        $this->request(User::MODULE, "POST", $data)->expect(400);
        $data = $this->mockRecord();
        unset($data["email"]);
        $this->request(User::MODULE, "POST", $data)->expect(400);
        $data = $this->mockRecord();
        $data["password"] = substr($data["password"], 0, 2);
        $this->request(User::MODULE, "POST", $data)->expect(400);
        $data = $this->mockRecord();
        $data["email"] = "askdjf@sadkfj";//invalid email - doesnt match regex
        $this->request(User::MODULE, "POST", $data)->expect(400);
        $data = $this->mockRecord();
        $data["login"] = "ab";//login too short
        $this->request(User::MODULE, "POST", $data)->expect(400);
        $data = $this->mockRecord();
        $data["login"] = "abcdefghijklmnopqrstuvwxyz";//login too long
        $this->request(User::MODULE, "POST", $data)->expect(400);
    }

    public function testRegister()
    {
        $this->init();
        $this->user->logout();

        $regData = $this->mockRegData();

        $this->request($this->getBasePath() . "/register", "GET", $regData)->expect(405);
        $this->request($this->getBasePath() . "/register", "DELETE", $regData)->expect(405);
        $this->request($this->getBasePath() . "/register", "PUT", $regData)->expect(405);

        $registeredData = $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(201)->getData();

        $this->authorizeAdmin();

        $this->deleteRecord($registeredData["id"]);
    }

    private function mockRegData()
    {
        $rand = rand(0, 10000);
        return [
            "login" => "kil_jaeden_$rand",
            "email" => "kil_jaeden_$rand@autotest.tymy.cz",
            "password" => "123465789",
            "callName" => "kil-jaeden",
        ];
    }

    public function testRegisterFailure()
    {
        $this->init();
        $this->user->logout();
        $rand = rand(0, 10000);

        $regData = $this->mockRegData();
        $regData["login"] = $this->config["user_test_login"];
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        $regData["email"] = $this->config["user_test_mail"];
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        unset($regData["password"]);
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        unset($regData["email"]);
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        $regData["password"] = substr($regData["password"], 0, 2);
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        $regData["email"] = "askdjf@sadkfj";//invalid email - doesnt match regex
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        $regData["login"] = "ab";//login too short
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        $regData = $this->mockRegData();
        $regData["login"] = "abcdefghijklmnopqrstuvwxyz";//login too long
        $this->request($this->getBasePath() . "/register", "POST", $regData)->expect(400);

        //reach registration limit
        $reg1Id = $this->request($this->getBasePath() . "/register", "POST", $this->mockRegData())->expect(201)->getData()["id"];
        $reg2Id = $this->request($this->getBasePath() . "/register", "POST", $this->mockRegData())->expect(201)->getData()["id"];
        $reg3Id = $this->request($this->getBasePath() . "/register", "POST", $this->mockRegData())->expect(201)->getData()["id"];
        $this->request($this->getBasePath() . "/register", "POST", $this->mockRegData())->expect(400);

        $this->authorizeAdmin();
        $this->deleteRecord($reg1Id);
        $this->deleteRecord($reg2Id);
        $this->deleteRecord($reg3Id);
    }

    public function testUpdateFailures()
    {
        $this->authorizeAdmin();
        $adminData = $this->request($this->getBasePath() . "/1", "PUT", ["roles" => ["USR"]])->expect(200)->getData();
        Assert::contains("SUPER", $adminData["roles"]);

        $userId = $this->createRecord();

        $this->authorizeUser();
        $this->request($this->getBasePath() . "/$userId", "PUT", $this->mockChanges())->expect(403);
        $myId = $this->config["user_test_id"];
        $this->request($this->getBasePath() . "/$userId", "PUT", $this->mockChanges())->expect(403);
        $this->request($this->getBasePath() . "/99999999999", "PUT", $this->mockChanges())->expect(404);
        $this->request($this->getBasePath() . "/$myId", "PUT", ["roles" => ["SUPER"]])->expect(403);
        $this->request($this->getBasePath() . "/$myId", "PUT", ["canEditCallName" => true])->expect(403);
        $this->request($this->getBasePath() . "/$myId", "PUT", ["status" => "MEMBER"])->expect(403);
        $this->request($this->getBasePath() . "/$myId", "PUT", ["login" => "new_login"])->expect(403);
        $this->request($this->getBasePath() . "/$myId", "PUT", ["callName" => "master_of_puppets"])->expect(403);
        $this->request($this->getBasePath() . "/$myId", "PUT", ["email" => $this->config["user_admin_mail"]])->expect(403);
    }

    public function testAvatar()
    {
        $this->authorizeUser();
        $myId = $this->config["user_test_id"];
        $this->request(
            $this->getBasePath() . "/$myId/avatar",
            "POST",
            "data:image/png;base64,"
        )->expect(200);

        $this->request(
            $this->getBasePath() . "/$myId/avatar",
            "GET",
            "data:image/png;base64,"
        )->expect(405);

        $this->request($this->getBasePath() . "/$myId/avatar", "POST")->expect(400);
        $this->request($this->getBasePath() . "/$myId/avatar", "POST", 1)->expect(400);

        $this->request(
            $this->getBasePath() . "/$myId/avatar",
            "POST",
            "data:text/plain;base64,"
        )->expect(400);
        $this->request(
            $this->getBasePath() . "/$myId/avatar",
            "POST",
            "ldkjfsldkfj"
        )->expect(400);

        $this->authorizeAdmin();
        $userId = $this->createRecord();

        $this->authorizeUser();
        $this->request(
            $this->getBasePath() . "/$userId/avatar",
            "POST",
            "data:image/png;base64,"
        )->expect(403);

        $this->request(
            $this->getBasePath() . "/$myId/avatar",
            "POST",
            "data:image/png;base64,"
        )->expect(200);
    }

    public function testLive()
    {
        $this->authorizeUser();
        $this->request("live")->expect(200, "array");
        $this->request("live", "POST")->expect(405);
    }

    public function createRecord()
    {
        return $this->recordManager->createUser();
    }

    public function mockRecord()
    {
        return $this->recordManager->mockUser();
    }

    /**
     * Override deleting function - user is not being deleted normally, he gets marked as deleted in status
     * @param int $recordId
     */
    public function deleteRecord($recordId)
    {
        $this->request($this->getBasePath() . "/$recordId", "PUT", ["status" => "DELETED"])->expect(200, "array");
    }

    /**
     * Load data list
     * @return SimpleResponse
     */
    private function getList()
    {
        $this->authorizeAdmin();
        return $this->request($this->getBasePath())->expect(200, "array");
    }

    protected function mockChanges(): array
    {
        return [
            "login" => "autotest_changed_" . rand(700, 900),
            "canLogin" => true,
            "canEditCallName" => false,
            "status" => "MEMBER",
            "firstName" => "Jarka",
            "lastName" => "Marná",
            "callName" => "Joža",
            "language" => "EN",
            "jerseyNumber" => (string)rand(100, 2000),
            "gender" => ["MALE", "FEMALE"][rand(0, 1)],
            "street" => "K Marastu 316",
            "city" => "Nový Krobuzon",
            "zipCode" => "91545",
            "birthDate" => "1980-04-22",
            "nameDayMonth" => 7,
            "nameDayDay" => 15,
            "accountNumber" => "987654321/0300",
        ];
    }
}

(new UserTest($container))->run();
